import React from "react";
import { motion } from "framer-motion";
import "./ContactUs.css";

import pfp from "../assets/pfp.png";
import khamsat from "../assets/Khamsat.png";
import facebook from "../assets/Facebook.png";
import soundeal from "../assets/Soundeal.jpg";
import whatsapp from "../assets/whatsapp.png";
import youtube from "../assets/Youtube.png";
import gmail from "../assets/Gmail.png";

function ContactUs() {
  // Animation variants
  const fadeUp = {
    hidden: { opacity: 0, y: 40 },
    visible: (i = 1) => ({
      opacity: 1,
      y: 0,
      transition: { delay: i * 0.2, duration: 0.6, ease: "easeOut" },
    }),
  };

  const slideInLeft = {
    hidden: { opacity: 0, x: -60 },
    visible: {
      opacity: 1,
      x: 0,
      transition: { duration: 0.8, ease: "easeOut" },
    },
  };

  const contacts = [
    { src: khamsat, label: "Khamsat" },
    { src: soundeal, label: "Soundeals" },
    { src: whatsapp, label: "WhatsApp" },
    { src: gmail, label: "Email" },
    { src: facebook, label: "Facebook" },
    { src: youtube, label: "YouTube" },
  ];

  return (
    <section id="contactus" className="py-5 overflow-hidden">
      <div className="container">
        {/* Title */}
        <div className="row">
          <div className="col-md-12 d-flex justify-content-center mb-5">
            <motion.h1
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, ease: "easeOut" }}
              viewport={{ once: true }}
            >
              تواصل معي
            </motion.h1>
          </div>
        </div>

        <div className="row d-flex align-items-center">
          {/* --- COLUMN 1: Contact Icons (on the right) --- */}
          <div className="col-md-6 d-flex justify-content-center">
            <motion.ul
              className="list-unstyled d-flex flex-wrap justify-content-center gap-4"
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              {contacts.map((item, index) => (
                <motion.li
                  key={index}
                  custom={index}
                  variants={fadeUp}
                  className="text-center"
                >
                  <a href="#link" className="text-decoration-none text-dark">
                    <motion.img
                      whileHover={{ scale: 1.1 }}
                      transition={{ type: "spring", stiffness: 200 }}
                      src={item.src}
                      alt={item.label}
                      style={{
                        width: "70px",
                        height: "70px",
                        objectFit: "contain",
                        marginBottom: "8px",
                      }}
                    />
                    <h5>{item.label}</h5>
                  </a>
                </motion.li>
              ))}
            </motion.ul>
          </div>

          {/* --- COLUMN 2: Profile Picture (on the left) --- */}
          <div className="col-md-6 d-flex justify-content-center">
            <motion.div
              className="text-center"
              variants={slideInLeft}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <motion.img
                src={pfp}
                alt="Profile"
                className="w-75 rounded-3 shadow"
                whileHover={{ scale: 1.05 }}
                transition={{ type: "spring", stiffness: 200 }}
              />
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ContactUs;
