import React from "react";
import "./ContactUs.css";
import pfp from "../assets/pfp.png";
import khamsat from "../assets/Khamsat.png";
import facebook from "../assets/Facebook.png";
import soundeal from "../assets/Soundeal.jpg";
import whatsapp from "../assets/whatsapp.png";
import youtube from "../assets/Youtube.png";
import gmail from "../assets/Gmail.png";

function ContactUs() {
  return (
    <section id="contactus">
      <div className="container">
        <div className="row">
          <div className="col-md-12 d-flex justify-content-center mb-5">
            <h1>تواصل معي</h1>
          </div>
        </div>

        {/* --- UPDATED ROW --- */}
        <div className="row d-flex align-items-center">
          
          {/* --- COLUMN 1: Profile Picture (Now on the left) --- */}
          <div className="col-md-6 d-flex justify-content-center">
            {/* Added a wrapper for the image and future audio bars */}
            <div className="profile-wrapper">
              <img src={pfp} alt="Profile" className="profile-pic" />
              {/* You can add the audio bars here later */}
            </div>
          </div>

          {/* --- COLUMN 2: Contact Box (Now on the right) --- */}
          <div className="col-md-6 d-flex justify-content-center">
            <div className="contact-box">
              <ul>
                <li>
                  <a href="#link"> {/* Added placeholder links */}
                    <img src={khamsat} alt="Khamsat" />
                  </a>
                  <h5>Khamsat</h5>
                </li>
                <li>
                  <a href="#link">
                    <img src={soundeal} alt="Soundeals" />
                  </a>
                  <h5>Soundeals</h5>
                </li>
                <li>
                  <a href="#link">
                    <img src={whatsapp} alt="WhatsApp" />
                  </a>
                  <h5>WhatsApp</h5>
                </li>
                <li>
                  <a href="#link">
                    <img src={gmail} alt="Gmail" /> {/* Removed w-25 class */}
                  </a>
                  <h5>Email</h5> {/* Changed from Gmail to Email */}
                </li>
                <li>
                  <a href="#link">
                    <img src={facebook} alt="Facebook" />
                  </a>
                  <h5>Facebook</h5>
                </li>
                <li>
                  <a href="#link">
                    <img src={youtube} alt="YouTube" />
                  </a>
                  <h5>YouTube</h5> {/* Changed from Youtube */}
                </li>
              </ul>
            </div>
          </div>

        </div>
      </div>
    </section>
  );
}

export default ContactUs;
